import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ServiceService, Service } from '../../../services/service.service';

@Component({
  selector: 'app-services',
  templateUrl: './services.component.html',
  styleUrls: ['./services.component.css']
})
export class ServicesComponent implements OnInit {
  services: Service[] = [];
  loading = false;
  error = '';

  constructor(private serviceService: ServiceService, private router: Router) {}

  ngOnInit(): void {
    this.fetchServices();
  }

  fetchServices(): void {
    this.loading = true;
    this.serviceService.getAll().subscribe({
      next: data => {
        this.services = data;
        this.loading = false;
      },
      error: () => {
        this.error = 'Error al cargar los servicios';
        this.loading = false;
      }
    });
  }

  editService(id: number): void {
    this.router.navigate(['/admin/services/edit', id]);
  }

  deleteService(id: number): void {
    if (confirm('¿Seguro que quieres eliminar este servicio?')) {
      this.serviceService.delete(id).subscribe({
        next: () => this.fetchServices(),
        error: () => alert('Error al eliminar el servicio')
      });
    }
  }

  createService(): void {
    this.router.navigate(['/admin/services/new']);
  }
}
