import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ServiceService, Service } from '../../../services/service.service';
import { HttpClient, HttpHeaders, HttpEventType } from '@angular/common/http';

@Component({
  selector: 'app-services',
  templateUrl: './services.component.html',
  styleUrls: ['./services.component.css']
})
export class ServicesComponent implements OnInit {
  services: Service[] = [];
  loading = false;
  error = '';
  selectedFile: File | null = null;
  uploading = false;
  progress = 0;
  uploadResult: any = null;

  constructor(
    private serviceService: ServiceService,
    private router: Router,
    private http: HttpClient
  ) {}

  ngOnInit(): void {
    this.fetchServices();
  }

  fetchServices(): void {
    this.loading = true;
    this.serviceService.getAll().subscribe({
      next: data => {
        this.services = data;
        this.loading = false;
      },
      error: () => {
        this.error = 'Error al cargar los servicios';
        this.loading = false;
      }
    });
  }

  editService(id: number): void {
    this.router.navigate(['/admin/services/edit', id]);
  }

  deleteService(id: number): void {
    if (confirm('¿Seguro que quieres eliminar este servicio?')) {
      this.serviceService.delete(id).subscribe({
        next: () => this.fetchServices(),
        error: () => alert('Error al eliminar el servicio')
      });
    }
  }

  createService(): void {
    this.router.navigate(['/admin/services/new']);
  }

  back(): void {
    this.router.navigate(['/admin']);
  }

  // --- Subida de Excel ---
  onFileSelected(event: any): void {
    const file = event.target.files[0];
    if (!file) return;
    if (file.size > 5 * 1024 * 1024) {
      alert('El archivo supera 5 MB.');
      return;
    }
    this.selectedFile = file;
  }

  uploadFile(): void {
    if (!this.selectedFile) return;

    this.uploading = true;
    this.progress = 0;
    this.uploadResult = null;

    const formData = new FormData();
    formData.append('files', this.selectedFile);
    this.selectedFile = null; // para permitir seleccionar de nuevo

    const token = localStorage.getItem('access_token');
    const headers = new HttpHeaders({ Authorization: `Bearer ${token}` });

    this.http.post('http://localhost:8000/upload/excel', formData, {
      headers,
      observe: 'events',
      reportProgress: true
    }).subscribe({
      next: event => {
        if (event.type === HttpEventType.UploadProgress && event.total) {
          this.progress = Math.round((event.loaded / event.total) * 100);
        } else if (event.type === HttpEventType.Response) {
          this.uploadResult = event.body;
          this.uploading = false;
          this.fetchServices();
        }
      },
      error: err => {
        this.uploading = false;
        alert(`Error al subir el archivo: ${err.error?.detail || 'Error desconocido'}`);
      }
    });
  }
}
