import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ServiceService, Service } from '../../../services/service.service';
import { HttpClient, HttpHeaders } from '@angular/common/http';

@Component({
  selector: 'app-services',
  templateUrl: './services.component.html',
  styleUrls: ['./services.component.css']
})
export class ServicesComponent implements OnInit {
  services: Service[] = [];
  loading = false;
  error = '';
  selectedFile: File | null = null;
  uploading = false;
  uploadResult: any = null;

  constructor(
    private serviceService: ServiceService,
    private router: Router,
    private http: HttpClient
  ) {}

  ngOnInit(): void {
    this.fetchServices();
  }

  fetchServices(): void {
    this.loading = true;
    this.serviceService.getAll().subscribe({
      next: data => {
        this.services = data;
        this.loading = false;
      },
      error: () => {
        this.error = 'Error al cargar los servicios';
        this.loading = false;
      }
    });
  }

  editService(id: number): void {
    this.router.navigate(['/admin/services/edit', id]);
  }

  deleteService(id: number): void {
    if (confirm('¿Seguro que quieres eliminar este servicio?')) {
      this.serviceService.delete(id).subscribe({
        next: () => this.fetchServices(),
        error: () => alert('Error al eliminar el servicio')
      });
    }
  }

  createService(): void {
    this.router.navigate(['/admin/services/new']);
  }

  back(): void {
    this.router.navigate(['/admin']);
  }

// --- Subida de Excel ---
onFileSelected(event: any): void {
  const file = event.target.files[0];
  if (!file) return;

  // Nuevo límite: 5 MB
  if (file.size > 5 * 1024 * 1024) {
    alert('El archivo supera 5 MB. El servidor lo rechazará.');
    return;
  }

  this.selectedFile = file;
}

uploadFile(): void {
  if (!this.selectedFile) return;

  this.uploading = true;
  this.uploadResult = null;

  const formData = new FormData();
  formData.append('files', this.selectedFile);

  const token = localStorage.getItem('access_token');
  const headers = new HttpHeaders({
    Authorization: `Bearer ${token}`
  });

  this.http.post('http://localhost:8000/upload/excel', formData, { headers })
    .subscribe({
      next: res => {
        this.uploadResult = res;
        this.uploading = false;
        this.fetchServices();
        alert('Archivo subido correctamente.');
      },
      error: err => {
        this.uploadResult = err.error;
        this.uploading = false;
        alert(`Error al subir el archivo: ${err.error?.detail || 'Error desconocido'}`);
      }
    });
}}
