export class ServicesComponent {
  constructor(serviceService, router, http) {
    this.serviceService = serviceService;
    this.router = router;
    this.http = http;
    this.services = [];
    this.loading = false;
    this.error = '';
    // Manejo de archivos
    this.selectedFiles = [];
    this.uploading = false;
    this.progress = 0;
    this.uploadResult = null;
  }
  ngOnInit() {
    this.fetchServices();
  }
  fetchServices() {
    this.loading = true;
    this.error = '';
    this.serviceService.getAll().subscribe({
      next: data => {
        this.services = data;
        this.loading = false;
      },
      error: err => {
        this.error = 'Error al cargar los servicios';
        this.loading = false;
        console.error('Error:', err);
      }
    });
  }
  editService(id) {
    this.router.navigate(['/admin/services/edit', id]);
  }
  deleteService(id) {
    if (confirm('¿Seguro que quieres eliminar este servicio?')) {
      this.serviceService.delete(id).subscribe({
        next: () => {
          this.fetchServices();
          alert('Servicio eliminado correctamente');
        },
        error: err => {
          alert('Error al eliminar el servicio');
          console.error('Error:', err);
        }
      });
    }
  }
  createService() {
    this.router.navigate(['/admin/services/new']);
  }
  back() {
    this.router.navigate(['/admin']);
  }
  // --- Manejo de archivos Excel ---
  onFileSelected(event) {
    const files = event.target.files;
    if (!files || files.length === 0) return;
    const newFiles = Array.from(files);
    // Validar límite de 5 archivos total
    if (this.selectedFiles.length + newFiles.length > 5) {
      alert('Solo puedes seleccionar hasta 5 archivos en total.');
      event.target.value = ''; // Limpiar input
      return;
    }
    // Validar extensión
    const invalidFiles = newFiles.filter(f => !f.name.toLowerCase().endsWith('.xls') && !f.name.toLowerCase().endsWith('.xlsx'));
    if (invalidFiles.length > 0) {
      alert(`Archivos con formato inválido: ${invalidFiles.map(f => f.name).join(', ')}`);
      event.target.value = '';
      return;
    }
    // Validar tamaño (5 MB por archivo)
    const oversized = newFiles.filter(f => f.size > 5 * 1024 * 1024);
    if (oversized.length > 0) {
      alert(`Archivos que superan 5 MB: ${oversized.map(f => f.name).join(', ')}`);
      event.target.value = '';
      return;
    }
    // Validar duplicados
    const duplicates = newFiles.filter(newFile => this.selectedFiles.some(existing => existing.file.name === newFile.name));
    if (duplicates.length > 0) {
      alert(`Archivos ya seleccionados: ${duplicates.map(f => f.name).join(', ')}`);
      event.target.value = '';
      return;
    }
    // Agregar archivos válidos
    newFiles.forEach(file => {
      this.selectedFiles.push({
        file: file,
        id: this.generateId()
      });
    });
    event.target.value = ''; // Limpiar input para permitir reselección
  }

  removeFile(id) {
    this.selectedFiles = this.selectedFiles.filter(item => item.id !== id);
  }
  clearAllFiles() {
    this.selectedFiles = [];
    this.uploadResult = null;
  }
  uploadFiles() {
    if (this.selectedFiles.length === 0) return;
    this.uploading = true;
    this.progress = 0;
    this.uploadResult = null;
    this.error = '';
    const formData = new FormData();
    this.selectedFiles.forEach(item => {
      formData.append('files', item.file);
    });
    const token = localStorage.getItem('access_token');
    const headers = new HttpHeaders({
      Authorization: `Bearer ${token}`
    });
    this.http.post('http://localhost:8000/upload/excel', formData, {
      headers,
      observe: 'events',
      reportProgress: true
    }).subscribe({
      next: event => {
        if (event.type === HttpEventType.UploadProgress && event.total) {
          this.progress = Math.round(event.loaded / event.total * 100);
        } else if (event.type === HttpEventType.Response) {
          this.uploadResult = event.body;
          this.uploading = false;
          this.progress = 100;
          this.fetchServices(); // Recargar lista de servicios
          // Mostrar mensaje de éxito
          if (this.uploadResult) {
            const {
              completed,
              failed
